import React, { useState } from "react";
import styled from "styled-components";
import AccountCircleOutlinedIcon from "@mui/icons-material/AccountCircleOutlined";
import SearchOutlinedIcon from "@mui/icons-material/SearchOutlined";
import VideoCallOutlinedIcon from "@mui/icons-material/VideoCallOutlined";
import NotificationsOutlinedIcon from "@mui/icons-material/NotificationsOutlined";
import AppsOutlinedIcon from "@mui/icons-material/AppsOutlined";
import { Link, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import Upload from "./Upload";
import { logout } from "../redux/userSlice"; // Import the logout action
import Metube from "../img/logo.png";

const Container = styled.div`
  position: sticky;
  top: 0;
  background-color: ${({ theme }) => theme.bgLighter};
  height: 56px;
  border-bottom: 1px solid ${({ theme }) => theme.bgLighter};
  display: flex;
  align-items: center;
  padding: 0 24px;
  z-index: 1000;
`;

const DropdownContainer = styled.div`
  position: absolute;
  top: 50px; /* Adjust the top position as needed */
  right: 0;
  background-color: ${({ theme }) => theme.bgLighter};
  border: 1px solid ${({ theme }) => theme.text};
  border-radius: 8px;
  box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.1);
  display: ${(props) => (props.open ? "block" : "none")};
  z-index: 1000;
`;

const DropdownItem = styled.div`
  padding: 10px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ theme }) => theme.soft};
    border-radius: 8px;
  }
`;

const LeftSection = styled.div`
  display: flex;
  align-items: center;
`;

// const Logo = styled.img`
//   height: 24px;
//   margin-right: 16px;
//   cursor: pointer;
// `;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  font-weight: bold;
  margin-bottom: 25px;
  font-size: 20px;
`;
const Img = styled.img`
  height: 30px;
`;
// const Wrapper = styled.div`
//   display: flex;
//   align-items: center;
//   justify-content: flex-end;
//   height: 100%;
//   padding: 0px 20px;
//   position: relative;
// `;

const Search = styled.div`
  width: 40%;
  position: absolute;
  left: 0px;
  right: 0px;
  margin: auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 5px;
  border: 1px solid ${({ theme }) => theme.text};
  border-radius: 30px;
  color: ${({ theme }) => theme.text};
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 8px;
  border-radius: 30px;
  outline: none;
  font-size: 14px;
  background-color: transparent;
  color: ${({ theme }) => theme.text};
`;

const SearchIcon = styled(SearchOutlinedIcon)`
  position: absolute;
  top: 50%;
  right: 8px;
  transform: translateY(-50%);
  cursor: pointer;
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  margin-left: auto;
`;

const IconWrapper = styled.div`
  margin-right: 24px;
  cursor: pointer;
`;

const Button = styled.button`
  padding: 5px 15px;
  background-color: transparent;
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: #3ea6ff;
  border-radius: 30px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
  &:hover {
    background-color: ${({ theme }) => theme.soft};
    border-radius: 30px;
  }
`;

// const User = styled.div`
//   display: flex;
//   align-items: center;
//   gap: 10px;
//   font-weight: 500;
//   color: ${({ theme }) => theme.text};
// `;

const Avatar = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  cursor: pointer;
`;

const Title = styled.h2`
  font-size: 14px;
  font-weight: 500;
  color: #aaaaaa;
  margin-bottom: 15px;
`;

const Navbar = () => {
  const { currentUser } = useSelector((state) => state.user);
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [q, setQ] = useState("");
  const dispatch = useDispatch(); // Initialize dispatch function


  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleAvatarClick = () => {
    setDropdownOpen(!dropdownOpen);
  };
  // Sign out function
  const handleSignOut = () => {
    dispatch(logout()); // Dispatch the logout action
    setDropdownOpen(false); // Close the dropdown after signing out
    // Additional sign-out logic if needed
  };

   const handleDeleteAccount = () => {
     // Add logic for deleting the user account
     setDropdownOpen(false); // Close the dropdown after deleting the account
     // Additional delete account logic if needed
   };

  return (
    <Container>
      <LeftSection>
        <Link to="/">
          <Logo>
            <Img src={Metube} alt="Metube Logo" />
            Metube
          </Logo>
        </Link>
        <Search>
          <SearchInput
            placeholder="Search"
            value={q}
            onChange={(e) => setQ(e.target.value)}
          />
          <SearchIcon onClick={() => navigate(`/search?q=${q}`)} />
        </Search>
      </LeftSection>
      <RightSection>
        <IconWrapper>
          <VideoCallOutlinedIcon />
        </IconWrapper>
        <IconWrapper>
          <AppsOutlinedIcon />
        </IconWrapper>
        <IconWrapper>
          <NotificationsOutlinedIcon />
        </IconWrapper>
        {currentUser ? (
          <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            <Avatar src={currentUser?.img} />
            <Link
              to={`/channel/${currentUser?.id}`}
              style={{ textDecoration: "none", color: "inherit" }}
            >
              {currentUser?.name}
              <Button onClick={handleSignOut}>SIGN OUT</Button>
            </Link>
          </div>
        ) : (
          <Link to="/signin" style={{ textDecoration: "none" }}>
            {/* <IconWrapper>
              <AccountCircleOutlinedIcon />
            </IconWrapper> */}
            <Button>
              <AccountCircleOutlinedIcon />
              SIGN IN
            </Button>
          </Link>
        )}
      </RightSection>
      {open && <Upload setOpen={setOpen} />}
    </Container>
  );
};

export default Navbar;
